mapper.update:
UPDATE web_user
SET 
update_time = NOW(),
nickname = #{nickname},
sex = #{sex},
at_area_id = #{atAreaId},
summary = #{summary},
head_img = #{headImg}

	, birthday = #{birthday}


	, phone = #{phone}


	, email = #{email}


	, user_level_id = #{userLevelId}

WHERE id = #{id};

UPDATE web_user SET update_time = NOW()

	, phone =#{phone} 


	,email = #{email} 

WHERE id = #{userId} ;

